pragma solidity ^0.8.0;

/**
 * @title Roles
 * @dev Library for managing addresses assigned to a Role.
 */
library Roles {
  struct Role {
    mapping (address => bool) bearer;
  }

  /**
   * @dev give an account access to this role
   */
  function add(Role storage role, address account) internal {
    require(account != address(0));
    role.bearer[account] = true;
  }

  /**
   * @dev remove an account's access to this role
   */
  function remove(Role storage role, address account) internal {
    require(account != address(0));
    role.bearer[account] = false;
  }

  /**
   * @dev check if an account has this role
   * @return bool
   */
  function has(Role storage role, address account)
    internal
    view
    returns (bool)
  {
    require(account != address(0));
    return role.bearer[account];
  }
}","keccak256":"0xacb14328e74061aaf20c9187f36b6878b3193680206cc94f73c2fd424e8b5a76","license":"GPL-3.0-or-later"},"contracts/WAGMI_CUP_SBT.sol":{"content":"//SPDX-License-Identifier:UNLICENSED
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "./MinterRole.sol";

contract  WAGMI_CUP_SBT is ERC721URIStorage, MinterRole {
    address public owner;

    struct sbt {
        string Title;
        string SbtCategory;
        // uint256 SbtId; 
        string DateOfIssue;
        string DeSocMembershipStatus;
        uint256 REPTokens;
        string RepTokenStatus;
        string Issuer;
        uint16 RunsScored;
        uint16 WicketsTaken;
        uint16 NoOfCatches;
        uint16 NoOfRunOuts;
        uint16 NoOfStumpings;
    }

    uint256 public SBT_ID = 1;
    mapping(uint256 => sbt) public sbtInfo;
    mapping(address => uint256) public userSbtID;
    mapping(address => sbt) public userSbtInfo;
    event SBT_mint(address minter, address receiver, uint256 SBT_ID, string tokenURI);
    event SBT_burn(uint256 SBT_ID);

    constructor() ERC721("WagmiCupPOAP_SoulBoundToken", "SBT") {
        owner = _msgSender();
    }


    function mint(address _to, sbt memory _sbt, uint256 rep, string memory _tokenURI)
        external
        onlyMinter(_msgSender())
    {
        _mint(_to, SBT_ID);
        _setTokenURI(SBT_ID, _tokenURI);
        _sbt.Title = "WAGMI Cup POAP";
        _sbt.SbtCategory = "Credential";
        // _sbt.SbtId =  SBT_ID;
        _sbt.DateOfIssue = "29th October 2022";
        _sbt.DeSocMembershipStatus = "Inactive";
        _sbt.REPTokens = rep;
        _sbt.RepTokenStatus = "Unattested";
        _sbt.Issuer = "EQ8 Desoc";
        userSbtID[_to] = SBT_ID;
        sbtInfo[SBT_ID] = _sbt;
        userSbtInfo[_to] = _sbt;

        emit SBT_mint(owner, _to, SBT_ID,_tokenURI);
        SBT_ID++;
    }

    function burn(uint256 _sbtId) external onlyMinter(_msgSender()) {
        _burn(_sbtId);
        emit SBT_burn(_sbtId);
    }

    function _transfer(
        address from,
        address to,
        uint256 _sbtId
    ) internal override {
        require(false, "NON-transferable");
        super._transfer(from, to, _sbtId);
    }
}","keccak256":"0xad0b8a4b129625bd6d1bd7388f69bab9e855e15bc9ff49e606e2888f0dd68152","license":"UNLICENSED"},"upgradeability/CustomOwnable.sol":{"content":"// SPDX-License-Identifier: Apache-2.0
import "../contracts/BasicMetaTransaction.sol";
pragma solidity ^0.8.0;

/**
 * @title CustomOwnable
 * @dev This contract has the owner address providing basic authorization control
 */
contract CustomOwnable is BasicMetaTransaction {
    /**
     * @dev Event to show ownership has been transferred
     * @param previousOwner representing the address of the previous owner
     * @param newOwner representing the address of the new owner
     */
    event OwnershipTransferred(address previousOwner, address newOwner);

    // Owner of the contract
    address private _owner;

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_msgSender() == owner(), "CustomOwnable: FORBIDDEN");
        _;
    }

    /**
     * @dev The constructor sets the original owner of the contract to the sender account.
     */
    constructor() {
        _setOwner(_msgSender());
    }

    /**
     * @dev Tells the address of the owner
     * @return the address of the owner
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Sets a new owner address
     */
    function _setOwner(address newOwner) internal {
        _owner = newOwner;
    }

    /**
     * @dev Allows the current owner to transfer control of the contract to a newOwner.
     * @param newOwner The address to transfer ownership to.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        require(newOwner != address(0), "CustomOwnable: FORBIDDEN");
        emit OwnershipTransferred(owner(), newOwner);
        _setOwner(newOwner);
    }
}
